import org.apache.commons.io.IOUtils
import java.nio.charset.StandardCharsets

def flowFiles = session.get(100)
if (!flowFiles || flowFiles.isEmpty()) return

def errorMap = [:].withDefault { [] }
def idealLogs = []

flowFiles.each { flowFile ->
    def text = ''
    session.read(flowFile, { inputStream ->
        text = IOUtils.toString(inputStream, StandardCharsets.UTF_8)
    } as InputStreamCallback)

    def lines = text.readLines()
    lines.each { line ->
        if (line.toLowerCase().contains("error")) {
            errorMap[line] += flowFile.getAttribute('filename')
        } else {
            idealLogs << [line: line, source: flowFile.getAttribute('filename')]
        }
    }
}

// Create merged error log if multiple FlowFiles have same error
def mergedErrorLines = errorMap.findAll { k, v -> v.size() > 1 }.keySet()
if (!mergedErrorLines.isEmpty()) {
    def content = mergedErrorLines.join('\n')
    def mergedFlowFile = session.create()
    mergedFlowFile = session.write(mergedFlowFile, { out ->
        out.write(content.getBytes(StandardCharsets.UTF_8))
    } as OutputStreamCallback)
    mergedFlowFile = session.putAttribute(mergedFlowFile, "filename", "merged-matching-errors.log")
    session.transfer(mergedFlowFile, REL_SUCCESS)
}

// Send ideal logs if any
if (!idealLogs.isEmpty()) {
    def content = idealLogs.collect { it.line }.join('\n')
    def idealFlowFile = session.create()
    idealFlowFile = session.write(idealFlowFile, { out ->
        out.write(content.getBytes(StandardCharsets.UTF_8))
    } as OutputStreamCallback)
    idealFlowFile = session.putAttribute(idealFlowFile, "filename", "ideal-logs.log")
    session.transfer(idealFlowFile, REL_SUCCESS)
}

// ✅ Remove original spark-log FlowFiles
flowFiles.each { session.remove(it) }
